class Solution {
    
    public int numTeams(int[] rating) {
        int count = 0;

        Map<Integer, Integer> increseStat = new HashMap<>();
        Map<Integer, Integer> decreaseStat = new HashMap<>();

        for(int i = 1; i < rating.length-1; i++) {
            int less = 0, more = 0;
            for(int j = i + 1; j < rating.length; j++) {
                if(rating[i] > rating[j]) {
                    less++;
                } else more++;
            }
            increseStat.put(i, more);
            decreaseStat.put(i,less);
        }

        for(int i = 0; i < rating.length-2; i++) {
            for(int j = i + 1; j< rating.length-1; j++) {
                if(rating[i]-rating[j] > 0) {
                    count = count + decreaseStat.get(j);
                } else if(rating[i]-rating[j] < 0) {
                    count = count + increseStat.get(j);
                }
            }
        }
        
        return count;
    }
}
